contracts/InsurancePool.sol.content:
// SPDX-License-Identifier: MIT
pragma solidity ^0.8.0;
import "../interfaces/IInsurancePool.sol";
import "@openzeppelin/contracts/access/Ownable.sol";
// import "@openzeppelin/contracts/security/ReentrancyGuard.sol";
import "../interfaces/IToken.sol";

error FAILED_TRANSACTION();

// Insurance Pool Contract
contract InsurancePool is IInsurancePool, Ownable {
    IToken public safeToken;
    mapping(address => LiquidityProvider) public providers;
    mapping(address => uint256) public rewardsOfProviders;
    uint256 public totalLiquidity;
    uint256 public rewardRate; // Rewards per second per safe
    address public policyManager;
    event PremiumReceived(address _addr, uint256 _premium);
    event ClaimPaid(address to, uint256 claimPaid);

    constructor(address _safeToken) Ownable(msg.sender){
        safeToken = IToken(_safeToken);
    }

    modifier onlyPolicyManager() {
        require(msg.sender == policyManager, "Caller is not the PolicyManager");
        _;
    }

    function setPolicyManager(address _policyManager) external onlyOwner {
        policyManager = _policyManager;
    }

    function provideLiquidity() external payable override{
        safeToken.approve(address(this),msg.value);       
        safeToken.transferFrom(address(this),msg.sender,msg.value);  
        updateRewards(msg.sender);
        providers[msg.sender].balance += msg.value;
        totalLiquidity += msg.value;
        emit LiquidityProvided(msg.sender, msg.value);
    }

    function checkTransferFrom() public{
        require(safeToken.allowance(msg.sender,address(this))>=1e16,"Less allowance than needed");
        require(safeToken.balanceOf(address(this))>1e16,"Insufficient funds");
        safeToken.approve(address(this),1e16);
        safeToken.transferFrom(address(this),msg.sender,1e16);
    }
    function withdrawLiquidity(uint256 amount) external override    {
        require(providers[msg.sender].balance >= amount, "Insufficient balance");
        updateRewards(msg.sender);
        providers[msg.sender].balance -= amount;
        totalLiquidity -= amount;
        safeToken.burn(msg.sender,amount);
        payable(msg.sender).transfer(amount);
        emit LiquidityWithdrawn(msg.sender, amount);
    }

    function claimRewards() external override    {
        updateRewards(msg.sender);
        uint256 rewards = providers[msg.sender].balance;
        if (rewards > 0) {
            providers[msg.sender].balance = 0;
            require(safeToken.transfer(msg.sender, rewards), "Transfer failed");
            (bool success,) = payable(msg.sender).call{value: rewards}("");
            if(!success){
                revert("EDU Tokens transfer failed");
            }
            emit RewardsClaimed(msg.sender, rewards);
        }
    }   

    function updateRewards(address provider) internal {
        uint256 timeElapsed = block.timestamp - providers[provider].lastUpdateTime;
        uint256 rewards = (providers[provider].balance * rewardRate * timeElapsed) / 1e18;
        providers[provider].balance += rewards;
        providers[provider].lastUpdateTime = block.timestamp;
        rewardsOfProviders[provider] += rewards;
    }

    function receivePremium(address from, uint256 amount) external payable override onlyPolicyManager {
        require(msg.value==amount,"Insufficient Funds Transferred");
        totalLiquidity += amount;
        emit PremiumReceived(from, amount);
    }

    function payClaim(address to, uint256 amount) external override onlyPolicyManager {
        require(totalLiquidity >= amount, "Insufficient liquidity for claim");
        totalLiquidity -= amount;
        (bool success,) = payable(to).call{value: amount}("");
        if(!success){
            revert("Transfer failed");
        }
        emit ClaimPaid(to, amount);
    }

    function getProviderBalance(address provider) external view override returns (uint256) {
        return providers[provider].balance;
    }

    function getTotalLiquidity() external view override returns (uint256) {
        return totalLiquidity;
    }

    function setRewardRate(uint256 _rewardRate) external onlyOwner {
        rewardRate = _rewardRate;
    }
}

contracts/PolicyManager.sol.content:
// SPDX-License-Identifier: MIT
pragma solidity ^0.8.0;

import "@openzeppelin/contracts/token/ERC20/IERC20.sol";
import "@openzeppelin/contracts/access/Ownable.sol";
import "../interfaces/IPolicyManager.sol";
import "../interfaces/IInsurancePool.sol";
import "../interfaces/IToken.sol";

// Policy Manager Contract
contract PolicyManager is IPolicyManager, Ownable {
    IToken public safeToken;
    IInsurancePool public insurancePool;
    mapping(uint256 => Policy) public policies;
    uint256 public nextPolicyId = 1;
    uint256 public nextClaimId = 1;
    mapping(address user=>uint256[] policyIdArray) public addressToPolicy;
    mapping(uint256 => Claim) public Claims;
    Claim[] public UnverifiedClaims;

    constructor(address _safeToken, address _insurancePool) Ownable(msg.sender){
        safeToken = IToken(_safeToken);
        insurancePool = IInsurancePool(_insurancePool);
    }

    modifier checkPolicyId(uint256 policyId){
        uint256[] storage policyIdArray = addressToPolicy[msg.sender];
        bool flag = false;
        for(uint256 index = 0;index<policyIdArray.length;index++){
            if(policyId==index){
                flag = true;
            }
        }
        if(!flag){
            revert("User does not purchased policy");
        }
        _;
    }

    function checkOwnershipOfPolicy(uint256 policyId,address owner) view public returns(bool){
         uint256[] storage policyIdArray = addressToPolicy[owner];
        bool flag = false;
        for(uint256 index = 1;index<policyIdArray.length;index++){
            if(policyId==index){
                flag = true;
            }
        }
        return flag;
    }

    function createPolicy(string memory _title,uint256 coverageAmount,string memory _description, uint256 expirationDate, string memory coverageType) external onlyOwner override     returns (uint256) {
        uint256 premium = calculatePremium(coverageAmount, expirationDate, coverageType);
        require(safeToken.transferFrom(msg.sender, address(insurancePool), premium), "Premium payment failed");

        uint256 policyId = nextPolicyId++;

        policies[policyId] = Policy({
            id:policyId,
            title:_title,
            description:_description,
            owner: msg.sender,
            coverageAmount: coverageAmount,
            premium: premium,
            expirationDate: expirationDate,
            isActive: true,
            coverageType: coverageType,
            nextPremiumDate: block.timestamp + 188 days
        });

        emit PolicyCreated(policyId, msg.sender, coverageAmount, premium, expirationDate);
        return policyId;
    }

    function purchasePolicy(uint256 policyId) payable external{
        Policy storage policy = policies[policyId];
        require(block.timestamp <= policy.expirationDate, "Policy expired");
        uint256 premium = policy.premium;      
        require(msg.value==premium,"Insufficient funds sent");
        insurancePool.receivePremium{value:msg.value}(msg.sender,msg.value);
        addressToPolicy[msg.sender].push(policyId);
        emit PolicyPurchased(policyId,msg.sender);
    }

    function claimPolicy(uint256 policyId,string memory _documentIPFSHash,string memory _description) external   {
        Claims[nextClaimId]  = Claim({
            claimId : nextClaimId,
            policyId : policyId,
            buyerAddress:msg.sender,
            documentIPFSHash : _documentIPFSHash,
            description : _description,
            isVerified : false,
            isPaid : false
        });
        UnverifiedClaims.push(Claims[nextClaimId]);
        nextClaimId++;
        emit ClaimCreated(policyId,msg.sender,_description);
    }

    function verifyClaim(uint256 claimId) external onlyOwner{
        Claim storage claim = Claims[claimId];
        uint256 policyId = claim.policyId;
        Policy storage policy = policies[policyId];
        bool flag = checkOwnershipOfPolicy(policyId, msg.sender);
        
        require(claimId>=0,"Claim does not exist");
        require(claimId<=UnverifiedClaims.length, "Claim does not exist");
        require(flag,"User doesn't purchased the policy");
        
        try insurancePool.payClaim(claim.buyerAddress, policy.coverageAmount){}
        catch{revert("Claim Pay Failed");}
        
        claim.isVerified=true;
        claim.isPaid=true;

        delete addressToPolicy[msg.sender][policyId];
        delete UnverifiedClaims[claimId];
        emit PolicyClaimed(policyId, msg.sender, policy.coverageAmount);
    }

    function payPremium(uint256 userPolicyId) checkPolicyId(userPolicyId) external payable     {
        Policy storage policy = policies[userPolicyId];
        require(block.timestamp <= policy.expirationDate, "Policy expired");
        uint256 premium = policy.premium;
        require(msg.value==premium,"Insufficient funds sent");
        insurancePool.receivePremium{value:msg.value}(msg.sender,msg.value);
        policy.nextPremiumDate = block.timestamp + 188 days;
        emit PremiumPaid(userPolicyId, msg.sender, premium);
    }

    function getPolicyDetails(uint256 policyId) external view returns (Policy memory) {
        return policies[policyId];
    }

    function getAllPolicies() external view returns (Policy[] memory) {
        uint256 policyCount = nextPolicyId;
        Policy[] memory policiesArray = new Policy[](policyCount);
        for(uint256 index=1; index < policyCount; index++){
            policiesArray[index] = policies[index];
        }
        return policiesArray;
     }

    function getAllPoliciesForUser(address user) public view returns (Policy[] memory) {
        uint256 policyCount = addressToPolicy[user].length;
        Policy[] memory policiesOfUser = new Policy[](policyCount);
        for(uint256 index=1; index < policyCount; index++){
            policiesOfUser[index] = policies[index];
        }
        return policiesOfUser;
     }

    function getAllUnverifiedClaims() external view returns(Claim[] memory){
        return UnverifiedClaims;
    }

    function calculatePremium(uint256 coverageAmount, uint256 duration, string memory coverageType) public view returns (uint256) {
        uint256 baseRate = 1; // 1% per year
        if (keccak256(bytes(coverageType)) == keccak256(bytes("exchange_hack"))) {
            baseRate = 2; // 2% per year for exchange hack coverage
        } else if (keccak256(bytes(coverageType)) == keccak256(bytes("defi_risk"))) {
            baseRate = 3; // 3% per year for general DeFi risk
        }
        return (coverageAmount * baseRate * (duration-block.timestamp)) / (365 days*100);
    }
}

contracts/ReducedManager.sol.content:
// SPDX-License-Identifier: MIT
pragma solidity ^0.8.0;

import "../interfaces/IPolicyManager.sol";
import "../interfaces/IInsurancePool.sol";
import "../interfaces/IToken.sol";

contract ReducedManager is IPolicyManager {
    IToken public immutable safeToken;
    IInsurancePool public immutable insurancePool;
    
    mapping(uint256 => Policy) public policies;
    mapping(uint256 => Claim) public claims;
    mapping(address=> uint256[]) public addressToPolicy;
    uint256 public nextPolicyId;
    uint256 public nextClaimId;

    error PremiumPaymentFailed();
    error ClaimDoesNotExist();
    error ClaimPayFailed();

    constructor(address _safeToken, address _insurancePool)  {
        safeToken = IToken(_safeToken);
        insurancePool = IInsurancePool(_insurancePool);
    }

    function createPolicy(string calldata _title, uint256 coverageAmount, string calldata _description, uint256 expirationDate, string calldata coverageType) external override returns (uint256) {
        uint256 premium = (coverageAmount * 2 * (expirationDate - block.timestamp)) / (365 days * 100);
        if (!safeToken.transferFrom(msg.sender, address(insurancePool), premium)) {
            revert PremiumPaymentFailed();
        }

        uint256 policyId = nextPolicyId++;
        policies[policyId] = Policy({
            id: policyId,
            title: _title,
            description: _description,
            owner: msg.sender,
            coverageAmount: coverageAmount,
            premium: premium,
            expirationDate: expirationDate,
            isActive: true,
            coverageType: coverageType,
            nextPremiumDate: block.timestamp + 188 days
        });

        emit PolicyCreated(policyId, msg.sender, coverageAmount, premium, expirationDate);
        return policyId;
    }

    function claimPolicy(uint256 policyId, string calldata _documentIPFSHash, string calldata _description) external {
        uint256 claimId = nextClaimId++;
        claims[claimId] = Claim({
            claimId: claimId,
            policyId: policyId,
            buyerAddress: msg.sender,
            documentIPFSHash: _documentIPFSHash,
            description: _description,
            isVerified: false,
            isPaid: false
        });
        emit ClaimCreated(policyId, msg.sender, _description);
    }

    function purchasePolicy(uint256 policyId) external payable {
        insurancePool.receivePremium{value: msg.value}(msg.sender, msg.value);
        addressToPolicy[msg.sender].push(policyId);
        emit PolicyPurchased(policyId, msg.sender);
    }

    function getTotallengthOfPolicies() external view returns(uint256){
        uint256 length = addressToPolicy[msg.sender].length;
        return length;
    }
    function verifyClaim(uint256 claimId) external {
        if (claimId >= nextClaimId) revert ClaimDoesNotExist();

        Claim storage claim = claims[claimId];
        Policy storage policy = policies[claim.policyId];
        
        try insurancePool.payClaim(claim.buyerAddress, policy.coverageAmount) {
            claim.isVerified = true;
            claim.isPaid = true;
            emit PolicyClaimed(claim.policyId, msg.sender, policy.coverageAmount);
        } catch {
            revert ClaimPayFailed();
        }
    }
}

